    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int ini = jSl1.getValue();
        int fin = jSl2.getValue();
        int pas = jSl3.getValue();
        String cont = "";
        
        
        for (int c=ini; c<=fin; c+=pas){
            if (c<fin) {
            cont += c + ", ";
            }else{
                cont += c;
            }
        } 
        
       jTxt.setText(String.format("%s", cont)); 
        
    }//GEN-LAST:event_jButton1ActionPerformed

